//POST requests
        POST   /notes    //create a new note

        //POST request body structure
        {
          "note_body" : string
        }

//PUT requests
        PUT    /notes    //update a specific note

        //PUT request body structure
        {
          "note" : {
            "note_id" : number,
            "note_body" : string,
            "position" : {
              "x" : number,
              "y" : number
            }
          }
        }

//DELETE requests
        DELETE /notes   //delete a specific note

        //DELETE request body structure
        {
          "note_id" : number
        }
